#define EFOR(A,B,C) for(int A=B;A<=C;A++)
#define RFOR(A,B,C) for(int A=B;A>=C;A--)
#define PB(A,B) A.push_back(B);
#define SORT(A) sort( A.begin(),A.end() );

using namespace std;

class ColorfulBoxesAndBalls {
public:
	int getMaximum(int, int, int, int, int);
};

int ColorfulBoxesAndBalls::getMaximum(int nR, int nB, int oR, int oB, int bC) {
	return ((nR>=nB)?(nR-nB)*oR:(nB-nR)*oB)+max(2*min(nR,nB)*bC,min(nR,nB)*(oR+oB));
}
